import react from "react";
import { SafeAreaView, Text, StyleSheet,View, ScrollView,TouchableOpacity,TextInput, Linking } from "react-native"
import React,{useState} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons/faLock'


import { LinearGradient, } from 'expo-linear-gradient';
import  '@react-navigation/native';
import { Evento } from "./Evento";

export const TelaPrincipal = () =>{
    return(

        
        <LinearGradient colors={['#fefffc', '#5eb6f5']} style={styles.container}>
                      
          
        <View style={{ flex: 1 }}>
        <View style={styles.tituloContainer}>
          <Text style={styles.titulo}>Eventos</Text>
          
        </View>
     

      {/* Conteúdo rolável */}
      <ScrollView style={{ flex: 1 }}>
        {/* Seu conteúdo aqui */}
        <Evento style={styles.evento}/>
       
      </ScrollView>

      <View style={styles.menu}>
        <Text style={styles.menu}>Menu</Text>
      </View>
    </View>
      
     
                    
        </LinearGradient>
     
    
  );


  

}

const styles = StyleSheet.create({
  
  tituloContainer:{
    
    justifyContent: 'space-between',
    borderRadius:30,
    flexDirection:'row',
    paddingLeft:20,
    paddingRight:20,
    

  },
  evento:{
    borderRadius:20,
  },
  titulo:{
    textAlign:'left',
    fontSize:40,
    fontWeight:'bold',
    marginTop:20,
    marginBottom:40,
  },
  container: {
    minWidth:"100%",
    flex: 1,
    justifyContent: 'center',
    padding: 20,
    marginTop:30,
    marginBottom:10,

  },
  menu:{ 
    height: 60,
    backgroundColor: '#5eb6f5',
    justifyContent: 'center', 
    alignItems: 'center', 
    padding:20
  }
  
  
 
    


}
